<script setup>
import { onMounted } from "vue";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

gsap.set([".coming-soon-left", ".coming-soon-right"], { willChange: "transform", force3D: true });

const handleHoverIn = () => {
  gsap.to(".color-d1034d", { color: "#1a1a1a", duration: 0.4 });
  gsap.to(".color-1a1a1a", { color: "#d1034d", duration: 0.4 });

  gsap.to(".bg-d1034d", { backgroundColor: "#1a1a1a", duration: 0.4 });
  gsap.to(".bg-1a1a1a", { backgroundColor: "#d1034d", duration: 0.4 });

  gsap.to(".fill-d1034d", { fill: "#d1034d", duration: 0.4 });

  gsap.to(".fill-fff", { fill: "#fff", duration: 0.4 });
};

const handleHoverOut = () => {
  gsap.to(".color-d1034d", { color: "#d1034d", duration: 0.4 });
  gsap.to(".color-1a1a1a", { color: "#1a1a1a", duration: 0.4 });

  gsap.to(".bg-d1034d", { backgroundColor: "#d1034d", duration: 0.4 });
  gsap.to(".bg-1a1a1a", { backgroundColor: "#1a1a1a", duration: 0.4 });

  gsap.to(".fill-d1034d", { fill: "#fff", duration: 0.4 });

  gsap.to(".fill-fff", { fill: "#000", duration: 0.4 });
};

onMounted(() => {
  const tl = gsap.timeline({
    defaults: { ease: "none" },
    scrollTrigger: {
      trigger: ".coming-soon-wrapper",
      start: "top top",
      end: "+=120%",
      scrub: true,
      pin: true,
      anticipatePin: 1,
      invalidateOnRefresh: true
    }
  });

  tl.to(".coming-soon-left", { xPercent: -100 }, 0)
    .to(".coming-soon-right", { xPercent: 100 }, 0)
    .to(".logo-small", { opacity: 0 }, 0)

  gsap.matchMedia().add("(max-width: 767px)", () => {
    tl.clear();
    tl.to(".coming-soon-left", { yPercent: -100 }, 0)
      .to(".coming-soon-right", { yPercent: 100 }, 0)
      .to(".logo-small", { opacity: 0 }, 0)
    ScrollTrigger.refresh();
  });
})
</script>

